import * as React from 'react';
import { DataGrid } from '@material-ui/data-grid';
import Button from '@material-ui/core/Button';
import axios from 'axios';
import { AUTH_ERRROR, DELETE_PROJECT } from '../actions/types'






// const rows = [
//   { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
//   { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
//   { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
//   { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
//   { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: 2 },
//   { id: 6, lastName: 'Melisandre', firstName: 'dave', age: 150 },
//   { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
//   { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
//   { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
// ];

// const rows = []

export default function DataTable(props) {

  const renderDetailsButton = (params) => {

    const onDelete = async () => {
      //console.log(params.row.id)

    return async dispatch => {
        try {
            const res = await axios.get('http://localhost:5001/projects/delete', params.row.id);
            dispatch({
                type: DELETE_PROJECT,
                payload: res.data.success
            });
        } catch (error) {
            dispatch({
                type: AUTH_ERROR,
                payload: 'Error'
            })
        }

    };
    }

    return (
      <strong>
        <Button
          variant="contained"
          color="primary"
          size="small"
          style={{ marginLeft: 16 }}
          onClick={onDelete}
        >
          Delete
        </Button>
      </strong>
    )
  }

  const columns = [
    { field: 'id', headerName: 'id' },
    { field: 'name', headerName: 'name' },
    { field: 'ref', headerName: 'reference' },
    {
      field: ' ',
      headerName: ' ',
      width: 150,
      renderCell: renderDetailsButton,
      disableClickEventBubbling: true,
    }
  ];

  return (
    <div style={{ height: 400, width: 500 }}>
      <DataGrid rows={props.rows} columns={columns} pageSize={10} />
    </div>
  );
}
